mapper.select:
select pi.pcode, pi.pname, pi.dept1no, pi.dept2no, pi.pmbti, pi.iloc, pi.pprice, pi.bname
from(
    select p.pcode, p.pname, p.dept1no,p.dept2no,p.pmbti, i.iloc, p.pprice, p.bno, p.bname,
          row_number() over
          (partition by p.pcode order by i.iloc desc) as rn 
    from img i right join (select pcode, pname, dept1no, dept2no, pprice, pd.bno, bname, pmbti from product pd join brand b on pd.bno=b.bno) p on p.pcode=i.pcode
    where i.itype='thumb' and p.pmbti=#{pmbti}
    ) pi right join product p on pi.pcode = p.pcode
    where pi.rn =1

SELECT mid, mbti, mbti_scores, stag
FROM member m JOIN skinmbti s on m.mbti=s.stype
WHERE mid=#{mid}
