mapper.select:
SELECT c.pcode, total, pname, pprice, bno, bname, itype, iloc FROM ( SELECT ROWNUM, pcode, total FROM ( SELECT PCODE, COUNT(*) AS TOTAL FROM (
SELECT PCODE, CASE WHEN PINGREDIENT LIKE '%'||#{item}||'%' THEN 1 ELSE 0 END AS CNT FROM PRODUCT WHERE DEPT1NO=#{dept1no} AND DEPT2NO=#{dept2no}
) WHERE CNT >= 1 AND PCODE != #{pcode} GROUP BY PCODE ORDER BY TOTAL DESC) P WHERE ROWNUM <= 8
) c JOIN similarCos_view s ON c.pcode=s.pcode ORDER BY total DESC
